import React, { useState, useEffect, useMemo } from 'react';
import '../styles/Combat.css';

function Combat() {
  const [loading, setLoading] = useState(true);
  const [playerHealth, setPlayerHealth] = useState(100);
  const [enemyHealth, setEnemyHealth] = useState(0);
  const [currentEnemy, setCurrentEnemy] = useState({});
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showDamage, setShowDamage] = useState(false);
  const [damageAmount, setDamageAmount] = useState(0);
  const [streak, setStreak] = useState(0);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [level, setLevel] = useState(1);
  
  // Sample data for enemies with enhanced properties
  const enemies = useMemo(() => [
    { 
      id: 1, 
      name: 'Math Monster', 
      image: 'üëæ', 
      type: 'math', 
      health: 100, 
      difficulty: 'easy',
      specialAbility: 'Doubles damage every third attack'
    },
    { 
      id: 2, 
      name: 'Science Sorcerer', 
      image: 'üß™', 
      type: 'science', 
      health: 120, 
      difficulty: 'medium',
      specialAbility: 'Heals 10% health on successful defense'
    },
    { 
      id: 3, 
      name: 'History Hydra', 
      image: 'üêâ', 
      type: 'history', 
      health: 150, 
      difficulty: 'hard',
      specialAbility: 'Grows stronger with each defeat'
    }
  ], []);
  
  // Enhanced questions with more variety
  const questions = useMemo(() => [
    {
      id: 1,
      text: 'What is 2 + 2?',
      correctAnswer: '4',
      difficulty: 'easy',
      type: 'math',
      points: 100
    },
    {
      id: 2,
      text: 'What is the chemical symbol for water?',
      correctAnswer: 'H2O',
      difficulty: 'medium',
      type: 'science',
      points: 200
    },
    {
      id: 3,
      text: 'Who was the first president of the United States?',
      correctAnswer: 'George Washington',
      difficulty: 'hard',
      type: 'history',
      points: 300
    }
  ], []);
  
  useEffect(() => {
    initializeCombat();
  }, [enemies, questions]);

  const initializeCombat = () => {
    const randomEnemy = enemies[Math.floor(Math.random() * enemies.length)];
    const randomQuestion = getQuestionByType(randomEnemy.type);
    
    setCurrentEnemy(randomEnemy);
    setCurrentQuestion(randomQuestion);
    setEnemyHealth(randomEnemy.health);
    setLoading(false);
    setGameOver(false);
    setPlayerHealth(100);
    setStreak(0);
    setScore(0);
    setLevel(1);
  };

  const getQuestionByType = (type) => {
    const typeQuestions = questions.filter(q => q.type === type);
    return typeQuestions[Math.floor(Math.random() * typeQuestions.length)] || questions[0];
  };

  const calculateDamage = (difficulty, currentStreak) => {
    let baseDamage = {
      'easy': 15,
      'medium': 25,
      'hard': 40
    }[difficulty] || 20;

    // Bonus damage for streak
    const streakBonus = Math.floor(currentStreak / 3) * 5;
    // Level bonus
    const levelBonus = (level - 1) * 2;

    return baseDamage + streakBonus + levelBonus;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (gameOver) {
      initializeCombat();
      return;
    }

    const isCorrect = answer.toLowerCase() === currentQuestion.correctAnswer.toLowerCase();
    
    if (isCorrect) {
      handleCorrectAnswer();
    } else {
      handleIncorrectAnswer();
    }

    // Get a new question of the same type as the current enemy
    const newQuestion = getQuestionByType(currentEnemy.type);
    setCurrentQuestion(newQuestion);
    setAnswer('');
  };

  const handleCorrectAnswer = () => {
    const newStreak = streak + 1;
    setStreak(newStreak);
    
    const damage = calculateDamage(currentQuestion.difficulty, newStreak);
    const newEnemyHealth = Math.max(0, enemyHealth - damage);
    
    // Show damage animation
    setDamageAmount(damage);
    setShowDamage(true);
    setTimeout(() => setShowDamage(false), 1000);
    
    setEnemyHealth(newEnemyHealth);
    setScore(score + currentQuestion.points * (1 + streak * 0.1));
    setMessage(`Streak x${newStreak}! You dealt ${damage} damage to ${currentEnemy.name}.`);
    setMessageType('success');
    
    if (newEnemyHealth <= 0) {
      handleEnemyDefeat();
    }
  };

  const handleIncorrectAnswer = () => {
    setStreak(0);
    const damage = 10 + Math.floor(level * 1.5); // Increased damage with level
    const newPlayerHealth = Math.max(0, playerHealth - damage);
    
    setPlayerHealth(newPlayerHealth);
    setMessage(`Incorrect! The answer was "${currentQuestion.correctAnswer}". ${currentEnemy.name} dealt ${damage} damage to you.`);
    setMessageType('error');
    
    if (newPlayerHealth <= 0) {
      handleGameOver();
    }
  };

  const handleEnemyDefeat = () => {
    const newLevel = level + 1;
    setLevel(newLevel);
    setMessage(`Victory! You defeated ${currentEnemy.name}! Level Up! (Level ${newLevel})`);
    
    setTimeout(() => {
      // Get a new enemy with increased difficulty
      const newEnemy = {
        ...enemies[Math.floor(Math.random() * enemies.length)],
        health: Math.floor(150 * (1 + (newLevel - 1) * 0.2)) // Health scales with level
      };
      setCurrentEnemy(newEnemy);
      setEnemyHealth(newEnemy.health);
      // Heal player a bit after victory
      setPlayerHealth(Math.min(100, playerHealth + 20));
    }, 2000);
  };

  const handleGameOver = () => {
    setGameOver(true);
    setMessage(`Game Over! Final Score: ${Math.floor(score)} | Level Reached: ${level}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Preparing for battle...</p>
      </div>
    );
  }
  
  return (
    <div className="combat-container">
      <div className="combat-header">
        <h1 className="combat-title">Combat Challenge</h1>
        <div className="combat-stats">
          <div className="stat">Level: {level}</div>
          <div className="stat">Score: {Math.floor(score)}</div>
          <div className="stat">Streak: {streak}</div>
        </div>
      </div>
      
      <div className={`combat-area card ${currentEnemy.type}`}>
        <div className="combat-status">
          <div className="health-bars">
            <div className="health-bar player-bar">
              <div className="health-label">Player</div>
              <div className="health-outer">
                <div 
                  className="health-inner player-health" 
                  style={{ width: `${playerHealth}%` }}
                ></div>
              </div>
              <div className="health-text">{playerHealth}/100</div>
            </div>
            
            <div className="health-bar enemy-bar">
              <div className="health-label">{currentEnemy.name}</div>
              <div className="health-outer">
                <div 
                  className="health-inner enemy-health" 
                  style={{ width: `${(enemyHealth / currentEnemy.health) * 100}%` }}
                ></div>
              </div>
              <div className="health-text">{enemyHealth}/{currentEnemy.health}</div>
            </div>
          </div>

          {message && (
            <div className={`message ${messageType}`}>{message}</div>
          )}
        </div>
        
        <div className="battle-scene">
          <div className="player-sprite">üßô</div>
          
          <div className="battle-arena">
            {showDamage && (
              <div className="damage-indicator">-{damageAmount}</div>
            )}
          </div>
          
          <div className="enemy-container">
            <div className={`enemy-sprite ${showDamage ? 'hit' : ''}`}>
              <span className="enemy-emoji">{currentEnemy.image}</span>
            </div>
            <div className="enemy-ability">{currentEnemy.specialAbility}</div>
          </div>
        </div>
        
        <div className="question-container">
          <div className="question-card">
            <div className="question-text">{currentQuestion.text}</div>
            <div className="question-info">
              <span className={`difficulty-${currentQuestion.difficulty}`}>
                {currentQuestion.difficulty.toUpperCase()}
              </span>
              <span className="question-points">+{currentQuestion.points} pts</span>
            </div>
          </div>
          
          <form onSubmit={handleSubmit} className="answer-form">
            <div className="input-group">
              <input 
                type="text" 
                value={answer} 
                onChange={(e) => setAnswer(e.target.value)} 
                placeholder="Enter your answer"
                className="answer-input"
                autoFocus
                disabled={gameOver}
              />
              <button 
                type="submit" 
                className={`attack-btn btn ${gameOver ? 'restart' : ''}`}
              >
                <span className="btn-text">
                  {gameOver ? 'Play Again' : 'Attack!'}
                </span>
                <span className="btn-icon">{gameOver ? 'üîÑ' : '‚öîÔ∏è'}</span>
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Combat;
