import React, { useState, useEffect, useMemo } from 'react';
import '../styles/Combat.css';

function Combat() {
  const [loading, setLoading] = useState(true);
  const [currentEnemy, setCurrentEnemy] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [enemyHealth, setEnemyHealth] = useState(100);
  const [playerHealth, setPlayerHealth] = useState(100);
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showDamage, setShowDamage] = useState(false);
  const [damageAmount, setDamageAmount] = useState(0);
  
  // Sample data - in a real app, this would come from an API or database
  const enemies = useMemo(() => [
    { id: 1, name: 'Math Monster', health: 100, image: 'ðŸ‘¹', type: 'danger' },
    { id: 2, name: 'Science Specter', health: 120, image: 'ðŸ‘»', type: 'secondary' },
    { id: 3, name: 'History Hydra', health: 150, image: 'ðŸ‰', type: 'warning' }
  ], []);
  
  const questions = useMemo(() => [
    { 
      id: 1, 
      text: 'What is 9 x 7?', 
      answer: '63', 
      difficulty: 'easy' 
    },
    { 
      id: 2, 
      text: 'What is the chemical symbol for water?', 
      answer: 'H2O', 
      difficulty: 'medium' 
    },
    { 
      id: 3, 
      text: 'In what year did World War II end?', 
      answer: '1945', 
      difficulty: 'medium' 
    }
  ], []);
  
  useEffect(() => {
    // Initialize combat with random enemy and question
    const randomEnemy = enemies[Math.floor(Math.random() * enemies.length)];
    const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
    
    setCurrentEnemy({...randomEnemy});
    setCurrentQuestion(randomQuestion);
    setEnemyHealth(randomEnemy.health);
    setLoading(false);
  }, [enemies, questions])

  const handleAnswerSubmit = (e) => {
    e.preventDefault();
    
    if (userAnswer === currentQuestion.correctAnswer) {
      // Correct answer
      const damage = 20 + Math.floor(Math.random() * 10); // Random damage between 20-30
      const newEnemyHealth = Math.max(0, enemyHealth - damage);
      
      setEnemyHealth(newEnemyHealth);
      setFeedback(`Correct! You dealt ${damage} damage to the ${currentEnemy.name}.`);
      
      if (newEnemyHealth <= 0) {
        setFeedback(`Victory! You defeated the ${currentEnemy.name}!`);
        // In a real app, you would update player XP, etc.
        setTimeout(() => {
          // Reset with a new enemy and question
          const randomEnemy = enemies[Math.floor(Math.random() * enemies.length)];
          const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
          
          setCurrentEnemy({...randomEnemy});
          setCurrentQuestion(randomQuestion);
          setEnemyHealth(randomEnemy.health);
          setFeedback('');
        }, 3000);
      } else {
        // Get a new question
        const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
        setCurrentQuestion(randomQuestion);
      }
    } else {
      // Incorrect answer
      const damage = currentEnemy.damage;
      const newPlayerHealth = Math.max(0, playerHealth - damage);
      
      setPlayerHealth(newPlayerHealth);
      setFeedback(`Incorrect! The ${currentEnemy.name} dealt ${damage} damage to you.`);
      
      if (newPlayerHealth <= 0) {
        setFeedback('Game Over! You were defeated.');
        // In a real app, you would handle game over logic
      }
    }
    
    setUserAnswer('');
  };

  if (loading) {
    return <div>Loading combat...</div>;
  }

  return (
    <div className="combat-container">
      <h1>Combat Arena</h1>
      
      <div className="health-bars">
        <div className="health-bar">
          <p>Your Health: {playerHealth}/100</p>
          <div className="health-bar-inner" style={{ width: `${playerHealth}%` }}></div>
        </div>
        <div className="health-bar">
          <p>{currentEnemy.name}'s Health: {enemyHealth}/{currentEnemy.health}</p>
          <div className="health-bar-inner enemy" style={{ width: `${(enemyHealth / currentEnemy.health) * 100}%` }}></div>
        </div>
      </div>
      
      <div className="enemy-container">
        <div className="enemy-sprite">{currentEnemy.sprite}</div>
        <h2>{currentEnemy.name}</h2>
      </div>
      
      {feedback && <div className="feedback-message">{feedback}</div>}
      
      <div className="question-container">
        <h3>Answer this question to attack:</h3>
        <p>{currentQuestion.text}</p>
        
        <form onSubmit={handleAnswerSubmit}>
          <div className="options-container">
            {currentQuestion.options.map((option, index) => (
              <div key={index} className="option">
                <input
                  type="radio"
                  id={`option-${index}`}
                  name="answer"
                  value={option}
                  checked={userAnswer === option}
                  onChange={(e) => setUserAnswer(e.target.value)}
                  required
                />
                <label htmlFor={`option-${index}`}>{option}</label>
              </div>
            ))}
          </div>
          <button type="submit" className="btn btn-primary">Attack!</button>
        </form>
      </div>
    </div>
  );
}

export default Combat;
