import React, { useState, useEffect, useMemo } from 'react';
import '../styles/Combat.css';

function Combat() {
  const [loading, setLoading] = useState(true);
  const [playerHealth, setPlayerHealth] = useState(100);
  const [enemyHealth, setEnemyHealth] = useState(0);
  const [currentEnemy, setCurrentEnemy] = useState({});
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showDamage, setShowDamage] = useState(false);
  const [damageAmount, setDamageAmount] = useState(0);
  
  // Sample data for enemies
  const enemies = useMemo(() => [
    { id: 1, name: 'Math Monster', image: 'ÔøΩ', type: 'math', health: 100, difficulty: 'easy' },
    { id: 2, name: 'Science Sorcerer', image: 'üß™', type: 'science', health: 120, difficulty: 'medium' },
    { id: 3, name: 'History Hydra', image: 'üêâ', type: 'history', health: 150, difficulty: 'hard' }
  ], []);
  
  // Sample data for questions
  const questions = useMemo(() => [
    {
      id: 1,
      text: 'What is 2 + 2?',
      correctAnswer: '4',
      difficulty: 'easy',
      type: 'math'
    },
    {
      id: 2,
      text: 'What is the chemical symbol for water?',
      correctAnswer: 'H2O',
      difficulty: 'medium',
      type: 'science'
    },
    {
      id: 3,
      text: 'Who was the first president of the United States?',
      correctAnswer: 'George Washington',
      difficulty: 'hard',
      type: 'history'
    }
  ], []);
  
  useEffect(() => {
    // Initialize combat with random enemy and question
    const randomEnemy = enemies[Math.floor(Math.random() * enemies.length)];
    const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
    
    setCurrentEnemy({...randomEnemy});
    setCurrentQuestion(randomQuestion);
    setEnemyHealth(randomEnemy.health);
    setLoading(false);
  }, [enemies, questions])

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (answer.trim().toLowerCase() === currentQuestion.answer.toLowerCase()) {
      // Correct answer
      const damage = 30; // Could vary based on difficulty
      const newEnemyHealth = Math.max(0, enemyHealth - damage);
      setEnemyHealth(newEnemyHealth);
      setMessage('Correct! You dealt damage to the enemy!');
      setMessageType('success');
      
      // Show damage animation
      setDamageAmount(damage);
      setShowDamage(true);
      setTimeout(() => setShowDamage(false), 1000);
      
      if (newEnemyHealth <= 0) {
        setMessage(`You defeated ${currentEnemy.name}!`);
        // In a real app, would give XP, update progress, etc.
        
        // Get new enemy and question
        setTimeout(() => {
          const randomEnemy = enemies[Math.floor(Math.random() * enemies.length)];
          const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
          
          setCurrentEnemy({...randomEnemy});
          setCurrentQuestion(randomQuestion);
          setEnemyHealth(randomEnemy.health);
          setMessage('');
        }, 2000);
      } else {
        // Get new question
        const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
        setCurrentQuestion(randomQuestion);
      }
    } else {
      // Incorrect answer
      const damage = 10; // Enemy attacks player
      const newPlayerHealth = Math.max(0, playerHealth - damage);
      setPlayerHealth(newPlayerHealth);
      setMessage('Incorrect! The enemy attacked you!');
      setMessageType('error');
      
      if (newPlayerHealth <= 0) {
        setMessage('Game Over! You were defeated!');
        // In a real app, would handle game over state
      }
    }
    
    setAnswer('');
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Preparing for battle...</p>
      </div>
    );
  }
  
  return (
    <div className="combat-container">
      <h1 className="combat-title">Combat Challenge</h1>
      
      <div className={`combat-area card ${currentEnemy.type}`}>
        <div className="combat-header">
          <div className="health-bars">
            <div className="health-bar player-bar">
              <div className="health-label">Player</div>
              <div className="health-outer">
                <div 
                  className="health-inner player-health" 
                  style={{ width: `${playerHealth}%` }}
                ></div>
              </div>
              <div className="health-text">{playerHealth}/100</div>
            </div>
            
            <div className="health-bar enemy-bar">
              <div className="health-label">{currentEnemy.name}</div>
              <div className="health-outer">
                <div 
                  className="health-inner enemy-health" 
                  style={{ width: `${(enemyHealth / currentEnemy.health) * 100}%` }}
                ></div>
              </div>
              <div className="health-text">{enemyHealth}/{currentEnemy.health}</div>
            </div>
          </div>
        </div>
        
        <div className="battle-scene">
          <div className="player-sprite">üßô</div>
          
          <div className="battle-arena">
            {showDamage && (
              <div className="damage-indicator">-{damageAmount}</div>
            )}
          </div>
          
          <div className="enemy-container">
            <div className={`enemy-sprite ${showDamage ? 'hit' : ''}`}>
              <span className="enemy-emoji">{currentEnemy.image}</span>
            </div>
          </div>
        </div>
        
        <div className="question-container">
          <h2 className="question-title">Answer this question to attack:</h2>
          <div className="question-card">
            <div className="question-text">{currentQuestion.text}</div>
            <div className="question-difficulty">
              Difficulty: <span className={`difficulty-${currentQuestion.difficulty}`}>{currentQuestion.difficulty}</span>
            </div>
          </div>
          
          <form onSubmit={handleSubmit} className="answer-form">
            <div className="input-group">
              <input 
                type="text" 
                value={answer} 
                onChange={(e) => setAnswer(e.target.value)} 
                placeholder="Enter your answer"
                className="answer-input"
                autoFocus
              />
              <button type="submit" className="attack-btn btn">
                <span className="btn-text">Attack!</span>
                <span className="btn-icon">‚öîÔ∏è</span>
              </button>
            </div>
          </form>
          
          {message && (
            <div className={`message ${messageType}`}>
              <div className="message-content">{message}</div>
            </div>
          )}
        </div>
      </div>
      
      <div className="combat-tips card mt-4">
        <h3>Combat Tips</h3>
        <ul className="tips-list">
          <li>Answer questions correctly to deal damage to enemies</li>
          <li>Each enemy has different health points</li>
          <li>Incorrect answers will result in you taking damage</li>
          <li>Defeat enemies to earn experience and level up</li>
        </ul>
      </div>
    </div>
  );
}

export default Combat;
