import React, { useState, useEffect, useMemo } from 'react';
import '../styles/Combat.css';

function Combat() {
  // State for game logic
  const [loading, setLoading] = useState(true);
  const [playerHealth, setPlayerHealth] = useState(100);
  const [enemyHealth, setEnemyHealth] = useState(0);
  const [currentEnemy, setCurrentEnemy] = useState({});
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [level, setLevel] = useState(1);
  
  // New: Enemies and questions
  const enemies = useMemo(() => [
    { id: 1, name: 'Quiz Knight', image: '�️', health: 100, flavor: 'A brave defender of knowledge.' },
    { id: 2, name: 'Puzzle Pixie', image: '�', health: 120, flavor: 'Sprinkles confusion with tricky questions.' },
    { id: 3, name: 'Riddle Rogue', image: '�️', health: 140, flavor: 'Strikes with cunning riddles.' }
  ], []);
  
  const questions = useMemo(() => [
    { id: 1, text: 'What is 5 x 6?', correctAnswer: '30', points: 100 },
    { id: 2, text: 'What planet is known as the Red Planet?', correctAnswer: 'Mars', points: 150 },
    { id: 3, text: 'Spell the word for water in chemistry.', correctAnswer: 'H2O', points: 120 }
  ], []);

  useEffect(() => {
    startNewBattle();
  }, []);

  const startNewBattle = () => {
    const enemy = enemies[Math.floor(Math.random() * enemies.length)];
    const question = questions[Math.floor(Math.random() * questions.length)];
    setCurrentEnemy(enemy);
    setCurrentQuestion(question);
    setEnemyHealth(enemy.health);
    setPlayerHealth(100);
    setScore(0);
    setLevel(1);
    setMessage('');
    setAnswer('');
    setGameOver(false);
    setLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (gameOver) {
      startNewBattle();
      return;
    }
    if (answer.trim().toLowerCase() === currentQuestion.correctAnswer.toLowerCase()) {
      const damage = 40;
      const newEnemyHealth = Math.max(0, enemyHealth - damage);
      setEnemyHealth(newEnemyHealth);
      setScore(score + currentQuestion.points);
      setMessage('Correct! You dealt ' + damage + ' damage.');
      if (newEnemyHealth <= 0) {
        setLevel(level + 1);
        setMessage('Enemy defeated! Level up!');
        setTimeout(() => startNewBattle(), 1500);
      }
    } else {
      const damage = 25;
      const newPlayerHealth = Math.max(0, playerHealth - damage);
      setPlayerHealth(newPlayerHealth);
      setMessage('Wrong! You took ' + damage + ' damage.');
      if (newPlayerHealth <= 0) {
        setGameOver(true);
        setMessage('Game Over! Final Score: ' + score);
      }
    }
    setAnswer('');
  };

  if (loading) {
    return (
      <div className="combat-new-loading">
        <div className="combat-new-spinner"></div>
        <p>Loading new battle...</p>
      </div>
    );
  }

  return (
    <div className="combat-new-root">
      <div className="combat-new-main-card">
        <div className="combat-new-enemy-section">
          <div className="combat-new-enemy-img">{currentEnemy.image}</div>
          <div className="combat-new-enemy-name">{currentEnemy.name}</div>
          <div className="combat-new-enemy-flavor">{currentEnemy.flavor}</div>
          <div className="combat-new-health-bar enemy">
            <div className="combat-new-health-inner" style={{width: `${(enemyHealth / (currentEnemy.health || 1)) * 100}%`}}></div>
            <span>{enemyHealth} / {currentEnemy.health}</span>
          </div>
        </div>
        <div className="combat-new-vs">VS</div>
        <div className="combat-new-player-section">
          <div className="combat-new-player-img">�‍🎓</div>
          <div className="combat-new-player-label">You</div>
          <div className="combat-new-health-bar player">
            <div className="combat-new-health-inner" style={{width: `${playerHealth}%`}}></div>
            <span>{playerHealth} / 100</span>
          </div>
        </div>
      </div>
      <div className="combat-new-question-card">
        <div className="combat-new-question">{currentQuestion.text}</div>
        <form className="combat-new-answer-form" onSubmit={handleSubmit}>
          <input
            className="combat-new-answer-input"
            type="text"
            value={answer}
            onChange={e => setAnswer(e.target.value)}
            placeholder="Type your answer..."
            disabled={gameOver}
            autoFocus
          />
          <button className="combat-new-action-btn" type="submit" disabled={gameOver && !loading}>
            {gameOver ? 'Restart' : 'Attack'}
          </button>
        </form>
        {message && <div className="combat-new-message">{message}</div>}
      </div>
      <div className="combat-new-footer">
        <span>Level: {level}</span>
        <span>Score: {score}</span>
      </div>
    </div>
  );
}

export default Combat;
